import Layout from "components/layout/Layout";

import { Container, Grid } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";

const useStyles = makeStyles((theme) => ({
  snsIcon: {
    width: "30px",
    height: "30px",

    [theme.breakpoints.down("xs")]: {
      width: "25px",
      height: "25px",
    },
    "&:hover": {
      color: theme.palette.info.main,
    },
  },
  avator: {
    width: "8em",
    height: "8em",
    boxShadow: "0px 0px 10px 1px #b2b2b28f",
  },
}));

const CrearClase = () => {
  const classes = useStyles();
  return (
    <Layout
      title="Crear Clase"
      description="Pagina para crear una clase nueva"
    >
      <Container maxWidth="md">
        <Grid container direction="column" alignItems="center" spacing={4}>
          <Grid item>
          <input  type="text" className="inputTitulo"  placeholder="Titulo de la clase" autoComplete="off" />  
          </Grid>

          <Grid item>
          <label className="d-inline">Pasos de Centro</label>
          </Grid>
          <Grid item>
          <input  type="text" className="inputObservacion1"  placeholder="Observaciones.." autoComplete="off" />
          </Grid>
          <Grid>
          <button className="btn btn_crear">Pasos</button>
          </Grid>

          <Grid item>
          <label className="d-inline">Pasos de equilibrio</label>
          </Grid>
          <Grid item>
          <input  type="text" className="inputObservacion1"  placeholder="Observaciones.." autoComplete="off" />
          </Grid>
          <Grid>
          <button className="btn btn_crear">Pasos</button>
          </Grid>

          <Grid item>
          <label className="d-inline">Danzas Tradicionales</label>
          </Grid>         
          <Grid>
          <button className="btn btn_crear">+</button>
          </Grid>
          <Grid>
          <button className="btn btn_crear">Guardar clase</button>
          </Grid>
        
          
        </Grid>
      </Container>
    </Layout>
  );
};

export default CrearClase;
