import Layout from "components/layout/Layout";
import { clases } from "data/clase";
import { Container, Grid } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import { Button } from "@material-ui/core";

const useStyles = makeStyles((theme) => ({
  snsIcon: {
    width: "30px",
    height: "30px",

    [theme.breakpoints.down("xs")]: {
      width: "25px",
      height: "25px",
    },
    "&:hover": {
      color: theme.palette.info.main,
    },
  },
  avator: {
    width: "8em",
    height: "8em",
    boxShadow: "0px 0px 10px 1px #b2b2b28f",
  },

}));

const Clases = () => {
  const classes = useStyles();
  return (
    <Layout
      title="Clases"
      description="Clases y Coreos"
    >
      <div className="">
        <div className="row margin-0">
          {clases.map((data,key) => { return (
            <Button key={key} href="/clases" className="col-12 margin-0 padding-0">
                <div className="row categoria">
                  <div className="col-7 titulo-clase">
                      {data.nombre}
                  </div>
                  <div className="col-5 datos-clase">
                    <p>fecha: {data.fecha}</p>
                    <p>Autor: {data.Autor}</p>
                  </div>
                  <div>
                    <img src=""/>
                  </div>
              </div>
            </Button>
          )})}
        </div>
      </div>
    </Layout>
  );
};

export default Clases;
